/* eslint-disable */
<template>
  <div class="data-container">
    <h1>Welcome, {{ username }}</h1>
    <p>{{ bio }}</p>
    <h2>Your Info:</h2>
    <ul class="data-list">
      <!-- 用 v-for 循环渲染展示数据 -->
      <li v-for="(item, index) in data" :key="index">
        {{ item.name }} - {{ item.description }}
      </li>
    </ul>
  </div>
</template>

<script>
export default {
  data() {
    return {
      data: [
        // 示例数据，这里可以用实际的数据代替
        { name: 'Project 1', description: 'Description of project 1' },
        { name: 'Project 2', description: 'Description of project 2' },
        { name: 'Project 3', description: 'Description of project 3' }
      ]
    };
  },
  computed: {
    // 从路由 query 中获取用户名和个人简介
    username() {
      return this.$route.query.username || 'Anonymous';
    },
    bio() {
      return this.$route.query.bio || 'No bio available';
    }
  }
};
</script>

<style scoped>
/* 容器样式 */
.data-container {
  max-width: 800px;
  margin: 0 auto;
  padding: 20px;
  font-family: 'Arial', sans-serif;
}

/* 标题样式 */
h1 {
  color: #333;
  font-size: 2rem;
  margin-bottom: 10px;
}

p {
  color: #666;
  font-size: 1.2rem;
  margin-bottom: 20px;
}

/* 数据列表样式 */
.data-list {
  list-style: none;
  padding: 0;
}

.data-list li {
  background-color: #f9f9f9;
  margin-bottom: 10px;
  padding: 15px;
  border-radius: 5px;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
  transition: background-color 0.3s ease;
}

.data-list li:hover {
  background-color: #e9e9e9;
}
</style>
