<template>
  <div class="user-container">
    <h1>Welcome, {{ username }}</h1>
    <p>{{ bio }}</p>
  </div>
</template>

<script>
export default {
  computed: {
    // 从路由 query 中获取用户名和个人简介
    username() {
      return this.$route.query.username || 'Anonymous';
    },
    bio() {
      return this.$route.query.bio || 'No bio available';
    }
  }
};
</script>

<style scoped>
/* 容器样式 */
.user-container {
  max-width: 600px;
  margin: 0 auto;
  padding: 20px;
  font-family: 'Arial', sans-serif;
  text-align: center;
}

/* 标题样式 */
h1 {
  color: #333;
  font-size: 2.5rem;
  margin-bottom: 10px;
}

/* 个人简介样式 */
p {
  color: #666;
  font-size: 1.2rem;
  margin-bottom: 20px;
}
</style>
